import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import BreadCrumb from "components/BreadCrumb";
import AssetItem from "components/AssetItem";
import AssetAuthor from "components/AssetAuthor";
import ApexCharts from "react-apexcharts";
import { useWeb3React } from "@web3-react/core";
import { Contract } from "@ethersproject/contracts";
import Market_INFO from "artifacts/contracts/Marketplace.sol/FlokinomicsNFTMarketplace.json";
import NFT_INFO from "artifacts/contracts/FlokinomicsNFT.sol/FlokinomicsNFT.json";
import {
  NFT_ADDRESS,
  CONTRACT_ADDRESS,
  PAYMENT_TOKEN,
  NFT_MARKET_ADDRESS,
  NFT_CONTRACT_ADDRESS,
} from "../../constants";
import { parseUnits } from "@ethersproject/units";
import Axios from "axios";
import Tabs from "./Tabs";
import Switch from "react-switch";
import "styles/activity.css";
import { useParams } from "react-router-dom";
import { firestore } from "../../firebase";
import { toast } from "react-toastify";
import Countdown from "react-countdown";
import moment from "moment";
import axios from "axios";
// import { BigNumber } from "ethers";

const breadcrumb = [
  { title: "Home", page: "/" },
  { title: "Item", page: "/item" },
];

function Item() {
  const { library, active, account } = useWeb3React();
  const { id } = useParams();
  const [price, setPrice] = useState(0);
  const [user, setUser] = useState({
    account: account,
    avatar: "assets/img/avatars/avatar.jpg",
    ownerAvatar: "assets/img/avatars/avatar.jpg",
    imageCover: "/assets/img/bg/bg.png",
    firstName: "User",
    lastName: "",
    nickName: "@user",
    bio: "",
    twitter: "",
    telegram: "",
    instagram: "",
    subscribe: "",
    followers: [],
  });
  const [currentPrice, setCurrentPrice] = useState(0);
  const [auctionLength, setAuctionLength] = useState("12");
  const [isProcessing, setIsProcessing] = useState(false);
  const [item, setItem] = useState({
    name: "",
    description: "",
    owner: account || null,
    time: 0,
    saleType: "fix",
    likes: [],
    price: 0,
    paymentType: "BNB",
  });
  const [isSale, setIsSale] = useState(false);
  const [isBid, setIsBid] = useState(false);
  const [isAccept, setIsAccept] = useState(false);
  const [auctionInfo, setAuctionInfo] = useState(null);
  const [historyData, setHistoryData] = useState([]);
  const [bidsData, setBidsData] = useState([]);
  const [rate, setRate] = useState(10);
  const [showUpdate, setShowUpdate] = useState(false);
  const [newPrice, setNewPrice] = useState(item.price);
  const [saleType, setSaleType] = useState(item.saleType);

  const [chartOption] = useState({
    chart: {
      width: 380,
      type: "pie",
    },
    labels: ["Creator's Royalty", "Platform fee", "Seller"],
    theme: {
      monochrome: {
        enabled: true,
        color: "#0000ff",
      },
    },
    responsive: [
      {
        breakpoint: 480,
        options: {
          chart: {
            id: "pie-chart",
            width: 200,
          },
          legend: {
            position: "bottom",
          },
        },
      },
    ],
  });

  const dispatch = useDispatch();

  const dispatchNftItem = (payload) => {
    dispatch({ type: "SET_SELECTED", payload });
    setItem(payload);
    setNewPrice(payload.price);
    setSaleType(payload.saleType);
  };

  const getHistory = async (item_id) => {
    const res = await firestore
      .collection("history")
      .where("nftId", "==", item_id)
      .get();
    let nft_history = [];
    for (let i = 0; i < res.docs.length; i++) {
      const x = res.docs[i];
      const temp = x.data();
      const user_info = (
        await firestore.collection("users").doc(temp.userId).get()
      ).data();
      const kcsPrice = temp.price;
      const timespace = parseInt((moment().valueOf() - temp.time) / 60000);
      const timeAgo =
        timespace < 1
          ? "less than a minute"
          : timespace < 60
          ? `${timespace} min ago`
          : timespace < 24 * 60
          ? `${parseInt(timespace / 60)} hrs ago`
          : moment(temp.time).format("YYYY/MM/DD");
      nft_history.push({
        avatar: user_info?.avatar || "/assets/img/avatars/avatar.jpg",
        kcsPrice,
        timeAgo,
        nickName: user_info?.nickName,
        verified: true,
        time: temp.time,
        actionType: temp.actionType,
      });
    }
    const history_data = nft_history.sort((a, b) => b.time - a.time);
    setHistoryData(history_data);
    setBidsData(history_data.filter((e) => e.actionType > 1));
  };
  const getItem = async (item_id) => {
    const res = await firestore.collection("nfts").doc(item_id).get();
    let nft_item = res.data();
    let nftInfo = { data: {} };
    try {
      nftInfo = await Axios.get(nft_item.tokenURI);
    } catch (err) {
      console.log(err);
    }
    dispatchNftItem({ id, ...user, ...nft_item, ...nftInfo.data });
    if (active) {
      const contract = new Contract(
        NFT_MARKET_ADDRESS,
        Market_INFO.abi,
        library.getSigner()
      );
      // const cur_price = await contract.listedMap(nft_item.tokenId);
      // console.log(cur_price);
      // const prov = ethers.getDefaultProvider();
      // const pp = await contract.balanceOf((await prov.getSigners())[0].address);

      // const bal = await prov.getBalance(account);

      if (nft_item?.tokenId !== 0) {
        const auction_info = await contract.auctions(nft_item.tokenId);
        setAuctionInfo(auction_info);
      }
    }
    setIsSale(nft_item?.isSale);
  };

  const updateSale = async (lock = false) => {
    setIsProcessing(true);
    try {
      const userExist = (await firestore.collection("users").doc(account).get())
        .exists;
      if (!userExist) {
        // await creatProfile(account);
        toast.error("Please create your profile first.");
        setIsProcessing(false);
        return;
      }

      const contract = new Contract(
        NFT_MARKET_ADDRESS,
        Market_INFO.abi,
        library.getSigner()
      );
      const nftContract = new Contract(
        NFT_ADDRESS,
        NFT_INFO.abi,
        library.getSigner()
      );
      if (item.isSale) {
        if (item.tokenId !== 0) {
          const res = await contract.closeTrade(item.tokenId);
          await res.wait();
        }
        await firestore.collection("nfts").doc(id).update({
          isSale: false,
          time: 0,
          saleType: "fix",
        });
        setItem({
          ...item,
          isSale: false,
          time: 0,
          saleType: "fix",
        });
        toast.success("Delisted from marketplace successfully");
        setShowUpdate(false);
        setIsProcessing(false);
      } else {
        if (newPrice <= 0) {
          toast.error("Price should not be zero.");
          setIsProcessing(false);
          return;
        }
        if (saleType === "auction") {
          await startAuction();
        } else {
          if (item.tokenId !== 0) {
            const isApproved = await nftContract.isApprovedForAll(
              account,
              NFT_MARKET_ADDRESS
            );
            if (!isApproved) {
              const approve = await nftContract.setApprovalForAll(
                NFT_MARKET_ADDRESS,
                true
              );
              await approve.wait();
            }
            const res = await contract.openTrade(
              item.tokenId,
              parseUnits(newPrice.toString()),
              item.paymentType
            );
            await res.wait();
          }
          await firestore
            .collection("nfts")
            .doc(id)
            .update({
              price: parseFloat(newPrice),
              saleType: "fix",
              isSale: true,
              time: 0,
            });
          setItem({
            ...item,
            price: parseFloat(newPrice),
            saleType: "fix",
            isSale: true,
            time: 0,
          });
          toast.success("Listed on marketplace successfully");
          setShowUpdate(false);
          setIsProcessing(false);
        }
      }
    } catch (err) {
      setIsSale((prev) => !prev);
      setShowUpdate(false);
      toast.error("Fail to update: " + err);
      setIsProcessing(false);
    }
  };

  const showUpdates = () => {
    if (isSale && !showUpdate) updateSale(true);
    setShowUpdate(!isSale);
    setIsSale((prev) => !prev);
  };

  // const creatProfile = async (user_account) => {
  //   const author = {
  //     avatar: "/assets/img/avatars/avatar.jpg",
  //     imageCover: "/assets/img/bg/bg.png",
  //     ownerAvatar: "/assets/img/avatars/avatar.jpg",
  //     firstName: "User",
  //     lastName: "",
  //     nickName: "@user",
  //     account: user_account,
  //     bio: "",
  //     twitter: "",
  //     telegram: "",
  //     instagram: "",
  //     subscribe: "",
  //     followers: [],
  //   };
  //   await firestore.collection("users").doc(user_account).set(author);
  // };

  const dispatchUser = async (user_id) => {
    if (user_id) {
      const userInfo = (
        await firestore.collection("users").doc(user_id).get()
      ).data();
      dispatch({ type: "SET_PROFILE", userInfo });
      setUser(userInfo);
    }
  };
  useEffect(() => {
    dispatchUser(account);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [account]);

  console.log(parseUnits(item.price.toString()));

  const buyNft = async () => {
    if (active) {
      setIsProcessing(true);
      try {
        const userExist = (
          await firestore.collection("users").doc(account).get()
        ).exists;
        if (!userExist) {
          // await creatProfile(account);
          toast.error("Please create your profile first.");
          setIsProcessing(false);
          return;
        }

        const contract = new Contract(
          CONTRACT_ADDRESS,
          Market_INFO.abi,
          library.getSigner()
        );
        const nftContract = new Contract(
          NFT_CONTRACT_ADDRESS,
          NFT_INFO.abi,
          library.getSigner()
        );
        const {
          tokenId,
          tokenURI,
          price,
          id,
          royalties,
          paymentType,
          creator,
        } = item;

        const isApproved = await nftContract.isApprovedForAll(
          account,
          NFT_MARKET_ADDRESS
        );
        if (!isApproved) {
          const approve = await nftContract.setApprovalForAll(
            NFT_MARKET_ADDRESS,
            true
          );
          await approve.wait();
        }

        // const approve = await nftContract.approve(
        //   CONTRACT_ADDRESS,
        //   parseUnits(price.toString())
        // );
        // await approve.wait();

        // const tokenContract = new Contract(
        //   PAYMENT_TOKEN[paymentType].tokenAddress,
        //   PAYMENT_TOKEN[paymentType].abi,
        //   library.getSigner()
        // );
        // const approve = await tokenContract.approve(
        //   NFT_MARKET_ADDRESS,
        //   parseUnits(price.toString())
        // );
        // await approve.wait();

        //console.log(isApproved);
        // if (!isApproved) {
        //   const approve = await nftContract.setApprovalForAll(
        //     NFT_MARKET_ADDRESS,
        //     true
        //   );
        //   await approve.wait();
        // }
        let res;
        // contract.setTokenAddress(
        //   paymentType,
        //   PAYMENT_TOKEN[paymentType].tokenAddress,
        //   NFT_ADDRESS
        // );

        if (tokenId === 0) {
          if (paymentType === "BNB") {
            console.log(
              JSON.stringify({
                tokenId,
                creator,
                price: parseUnits(price.toString()),
                paymentType,
                royalties,
                tokenURI,
                from: account,
                value: parseUnits(price.toString()),
              }),
              null,
              2
            );

            res = await contract.buyNew(
              tokenId,
              creator,
              parseUnits(price.toString()),
              paymentType,
              royalties,
              tokenURI,
              { from: account, value: parseUnits(price.toString()) }
            );
            // res = await contract.buy(
            //   tokenId,
            //   parseUnits(price.toString()),
            //   paymentType,
            //   { from: account, value: parseUnits(price.toString()) }
            // );
          } else {
            const tokenContract = new Contract(
              PAYMENT_TOKEN[paymentType].tokenAddress,
              PAYMENT_TOKEN[paymentType].abi,
              library.getSigner()
            );
            const approve = await tokenContract.approve(
              NFT_CONTRACT_ADDRESS,
              parseUnits(price.toString())
            );
            await approve.wait();

            res = await contract.buyNew(
              tokenId,
              creator,
              parseUnits(price.toString()),
              paymentType,
              royalties,
              tokenURI,
              { from: account, value: parseUnits(price.toString()) }
            );
          }
        } else {
          if (paymentType === "BNB") {
            res = await contract.buy(
              tokenId,
              parseUnits(price.toString()),
              paymentType,
              { from: account, value: parseUnits(price.toString()) }
            );
          } else {
            const tokenContract = new Contract(
              PAYMENT_TOKEN[paymentType].tokenAddress,
              PAYMENT_TOKEN[paymentType].abi,
              library.getSigner()
            );
            const approve = await tokenContract.approve(
              NFT_MARKET_ADDRESS,
              parseUnits(price.toString())
            );
            await approve.wait();
            res = await contract.buy(
              tokenId,
              parseUnits(price.toString()),
              paymentType,
              { from: account, value: parseUnits(price.toString()) }
            );
          }
        }

        res
          .wait()
          .then(async (result) => {
            console.log(result);
            let nftId = 0;
            const events = result?.events;
            if (events.length > 0) {
              nftId = events[events.length - 1].args["nftID"].toString();
            }
            await firestore
              .collection("nfts")
              .doc(id)
              .update({
                tokenId: parseInt(nftId),
                isSale: false,
                owner: account,
                time: 0,
                saleType: "fix",
              });
            const old_owner = item.owner;
            dispatchNftItem({
              ...item,
              tokenId: parseInt(nftId),
              isSale: false,
              owner: account,
              time: 0,
              saleType: "fix",
            });
            setIsSale(false);

            firestore.collection("history").add({
              userId: account,
              oldUserId: old_owner,
              nftId: id,
              actionType: 1,
              price: parseFloat(price),
              time: moment().valueOf(),
            });
            getHistory(id);
            setIsProcessing(false);
            setIsAccept(false);
            toast.success("You bought a NFT successfully");
          })
          .catch((err) => {
            toast.error("Failed to Buy");
            setIsProcessing(false);
          });
      } catch (err) {
        console.log(err);
        toast.error(
          err?.data?.message ? err.data.message : "Failed to buy NFT"
        );
        setIsProcessing(false);
      }
    } else {
      toast.error("Please connect your wallet first.");
      setIsProcessing(false);
    }
  };

  const bidNft = async () => {
    if (active) {
      if (
        price < item.price ||
        (parseFloat(auctionInfo.amount) > 0 && price < item.price * 1.1)
      ) {
        toast.error("Bid amount must not less than minimum bid");
        return;
      }
      setIsProcessing(true);
      try {
        const userExist = (
          await firestore.collection("users").doc(account).get()
        ).exists;
        if (!userExist) {
          // await creatProfile(account);
          toast.error("Please create your profile first.");
          setIsProcessing(false);
          return;
        }
        const contract = new Contract(
          NFT_MARKET_ADDRESS,
          Market_INFO.abi,
          library.getSigner()
        );

        const { tokenId, paymentType } = item;
        let res;
        if (paymentType === "BNB") {
          res = await contract.createBid(
            tokenId,
            paymentType,
            parseUnits(price.toString()),
            {
              from: account,
              value: parseUnits(price.toString()),
            }
          );
        } else {
          res = await contract.createBid(
            tokenId,
            paymentType,
            parseUnits(price.toString()),
            {
              from: account,
              value: parseUnits(price.toString()),
            }
          );
        }

        res
          .wait()
          .then(async (result) => {
            const auction_info = await contract.auctions(tokenId);
            await firestore
              .collection("nfts")
              .doc(id)
              .update({
                price: parseFloat(price),
                // owner: account,
                saleType: "auction",
                time:
                  (parseInt(auction_info.duration) +
                    parseInt(auction_info.firstBidTime)) *
                  1000,
              });
            dispatchNftItem({
              ...item,
              price: parseFloat(price),
              // owner: account,
              saleType: "auction",
              time:
                (parseInt(auction_info.duration) +
                  parseInt(auction_info.firstBidTime)) *
                1000,
            });
            setAuctionInfo(null);
            setIsAccept(false);
            toast.success("You have placed bid this auction");

            firestore.collection("history").add({
              userId: account,
              oldUserId: account,
              nftId: id,
              actionType: 2,
              price: parseFloat(price),
              time: moment().valueOf(),
            });
            getHistory(id);
            setIsBid(false);
            setIsProcessing(false);
          })
          .catch((err) => {
            toast.error("Failed to bid auction");
            setIsBid(false);
            setIsProcessing(false);
          });
      } catch (err) {
        toast.error("Failed to bid auction");
        setIsBid(false);
        setIsProcessing(false);
      }
    } else {
      toast.error("Please connect your wallet first.");
    }
  };

  const startAuction = async () => {
    if (active) {
      setIsProcessing(true);
      try {
        const contract = new Contract(
          NFT_MARKET_ADDRESS,
          Market_INFO.abi,
          library.getSigner()
        );
        const nftContract = new Contract(
          NFT_ADDRESS,
          NFT_INFO.abi,
          library.getSigner()
        );
        await nftContract.nftContract;

        const { tokenId, tokenURI, paymentType } = item;
        const auction_length = parseInt(auctionLength) * 3600;
        // const auction_length = 1200;
        // const isApproved = await nftContract.isApprovedForAll(
        //   account,
        //   NFT_MARKET_ADDRESS
        // );
        // console.log(
        //   "isApproved",
        //   isApproved,
        //   newPrice,
        //   auction_length,
        //   account
        // );
        // if (!isApproved) {
        //   const approve = await nftContract.setApprovalForAll(
        //     NFT_MARKET_ADDRESS,
        //     true
        //   );
        //   await approve.wait();
        // }
        const res = await contract.createAuction(
          tokenId,
          tokenId === 0,
          tokenURI,
          auction_length,
          parseUnits(newPrice.toString()),
          paymentType,
          account,
          { from: account, value: parseUnits(newPrice.toString()) }
        );
        res
          .wait()
          .then(async (result) => {
            const events = result?.events;
            if (events.length > 0) {
              const args = events[events.length - 1].args;
              await firestore
                .collection("nfts")
                .doc(id)
                .update({
                  isSale: true,
                  saleType: "auction",
                  tokenId: parseInt(args.tokenId),
                  auctionCreator: account,
                  auctionLength: auction_length,
                  time:
                    (parseInt(args.duration) + parseInt(args.auctionStart)) *
                    1000,
                  price: parseFloat(newPrice),
                });
              dispatchNftItem({
                ...item,
                isSale: true,
                saleType: "auction",
                auctionLength: auction_length,
                auctionCreator: account,
                tokenId: parseInt(args.tokenId),
                time:
                  (parseInt(args.duration) + parseInt(args.auctionStart)) *
                  1000,
                price: parseFloat(newPrice),
              });
              const auction_info = await contract.auctions(
                parseInt(args.tokenId)
              );
              setAuctionInfo(auction_info);
              firestore.collection("history").add({
                userId: account,
                oldUserId: account,
                nftId: id,
                actionType: 3,
                price: parseFloat(newPrice),
                time: moment().valueOf(),
              });
              getHistory(id);
              setShowUpdate(false);
              setIsProcessing(false);
              setIsAccept(false);
              toast.success("You create an auction");
            }
          })
          .catch((err) => {
            setIsSale(false);
            setShowUpdate(false);
            setIsProcessing(false);
            toast.error("Failed to create auction");
          });
      } catch (err) {
        setIsSale(false);
        toast.error("Failed to create auction");
        setShowUpdate(false);
        setIsProcessing(false);
      }
    } else {
      toast.error("Please connect your wallet first");
      setIsProcessing(false);
    }
  };

  const cancelAuction = async () => {
    if (active) {
      setIsProcessing(true);
      try {
        const contract = new Contract(
          NFT_MARKET_ADDRESS,
          Market_INFO.abi,
          library.getSigner()
        );
        const { tokenId } = item;
        const res = await contract.cancelAuction(tokenId);
        res
          .wait()
          .then(async (result) => {
            await firestore.collection("nfts").doc(id).update({
              time: 0,
              saleType: "fix",
              auctionCreator: null,
              isSale: false,
            });
            dispatchNftItem({
              ...item,
              saleType: "fix",
              time: 0,
              auctionCreator: null,
              isSale: false,
            });
            setIsSale(false);
            setAuctionInfo(null);

            firestore.collection("history").add({
              userId: account,
              oldUserId: account,
              nftId: id,
              actionType: 5,
              price: item.price,
              time: moment().valueOf(),
            });
            getHistory(id);
            toast.success("Auction is canceled");
            setIsProcessing(false);
            setIsAccept(false);
          })
          .catch((err) => {
            toast.error("Failed to cancel auction");
            setIsProcessing(false);
          });
      } catch (err) {
        toast.error("Failed to cancel auction");
        setIsProcessing(false);
      }
    } else {
      toast.error("Please connect your wallet first");
    }
  };

  const endAuction = async () => {
    if (active) {
      setIsProcessing(true);
      try {
        const contract = new Contract(
          NFT_MARKET_ADDRESS,
          Market_INFO.abi,
          library.getSigner()
        );
        const nftContract = new Contract(
          NFT_ADDRESS,
          NFT_INFO.abi,
          library.getSigner()
        );
        const { tokenId } = item;
        const isApproved = await nftContract.isApprovedForAll(
          account,
          NFT_MARKET_ADDRESS
        );
        if (!isApproved) {
          const approve = await nftContract.setApprovalForAll(
            NFT_MARKET_ADDRESS,
            true
          );
          await approve.wait();
        }
        const res = await contract.endAuction(tokenId);
        res
          .wait()
          .then(async (result) => {
            await firestore
              .collection("nfts")
              .doc(id)
              .update({
                saleType: "fix",
                time: 0,
                auctionLength: null,
                auctionCreator: null,
                isSale: false,
                owner:
                  auctionInfo.bidder !==
                  "0x0000000000000000000000000000000000000000"
                    ? auctionInfo.bidder
                    : auctionInfo.Creator,
              });
            dispatchNftItem({
              ...item,
              saleType: "fix",
              time: 0,
              auctionLength: null,
              auctionCreator: null,
              isSale: false,
              owner:
                auctionInfo.bidder !==
                "0x0000000000000000000000000000000000000000"
                  ? auctionInfo.bidder
                  : auctionInfo.Creator,
            });
            setIsSale(false);

            if (
              auctionInfo.bidder !==
              "0x0000000000000000000000000000000000000000"
            )
              firestore.collection("history").add({
                userId: auctionInfo.bidder,
                oldUserId: auctionInfo.Creator,
                nftId: id,
                actionType: 1,
                price: item.price,
                time: moment().valueOf(),
              });
            firestore.collection("history").add({
              userId: account,
              oldUserId: account,
              nftId: id,
              actionType: 4,
              price: item.price,
              time: moment().valueOf(),
            });
            getHistory(id);
            setAuctionInfo(null);
            setIsAccept(false);
            toast.success("Auction is completed");
            setIsProcessing(false);
          })
          .catch((err) => {
            toast.error("Failed to complete auction");
            setIsProcessing(false);
          });
      } catch (err) {
        toast.error("Failed to complete auction");
        setIsProcessing(false);
      }
    } else {
      toast.error("Please connect your wallet first.");
    }
  };

