import React, { useState, useEffect } from "react";

import { useWeb3React } from "@web3-react/core";
import { InjectedConnector } from "@web3-react/injected-connector";
import { useEagerConnect } from "../../hooks/useEagerConnect";
import { UnsupportedChainIdError } from "@web3-react/core";
import { useInactiveListener } from "../../hooks/useInactiveListener";
import Switch from "react-switch";

import "styles/header.css";
import EthBalance from "components/EthBalance";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { firestore } from "../../firebase";
import { toast } from "react-toastify";
import useSWR from "swr";
import { DefaultNetwork } from "../../constants/index";
import { setTheme } from "../../actions/themeAction";

import logo from "assets/img/BleuFi_Logo.png";
export const injectedConnector = new InjectedConnector({
  supportedChainIds: [DefaultNetwork, 1, 56, 97],
});

function Header() {
  const { error, account, library, activate, active, connector, deactivate } =
    useWeb3React();

  const { theme } = useSelector((state) => state.web3);

  const history = useHistory();

  // eslint-disable-next-line no-unused-vars
  const [wrongNetwork, setWrongNetwork] = useState(false);
  const [user, setUser] = useState({
    account: account,
    avatar: "assets/img/avatars/avatar.jpg",
    imageCover: "/assets/img/bg/bg.png",
    firstName: "User",
    lastName: "",
    nickName: "@user",
    bio: "",
    twitter: "",
    telegram: "",
    instagram: "",
    subscribe: "",
    followers: [],
  });
  const dispatch = useDispatch();

  const fetcher =
    (library) =>
    (...args) => {
      const [method, ...params] = args;
      return library[method](...params);
    };
  // eslint-disable-next-line no-unused-vars
  const { data: balance } = useSWR(["getBalance", account, "latest"], {
    fetcher: fetcher(library),
  });
  const isUnsupportedChainIdError = error instanceof UnsupportedChainIdError;
  useEffect(() => {
    setWrongNetwork(isUnsupportedChainIdError);
  }, [isUnsupportedChainIdError]);

  const connectWallet = async () => {
    if (active) {
      try {
        deactivate();
      } catch (e) {
        console.log(e);
      }
    } else {
      await activate(injectedConnector, (err) => console.log(err));
      window.ethereum.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId:'0x38' }],
      })
    }

    //history.push("/signin");
  };

  const [activatingConnector, setActivatingConnector] = useState();

  useEffect(() => {
    if (activatingConnector && activatingConnector === connector) {
      setActivatingConnector(undefined);
    }
  }, [activatingConnector, connector]);

  const dispatchUser = async (user_id) => {
    if (user_id) {
      const userInfo = (
        await firestore.collection("users").doc(user_id).get()
      ).data();
      console.log('------', userInfo);
      if (userInfo) {
        dispatch({ type: "SET_PROFILE", userInfo });
        setUser(userInfo);
        history.push('/')
      } else if (active) {
        toast.info("Please set up your profile before you use the marketplace");
        history.push('/creator/' + account + '?tab=setting')
      }
    }
  };

  const updateTheme = (changeTheme = "light") => {
    dispatch(setTheme(changeTheme));
  };

  useEffect(() => {
    if (active && account) {
      dispatchUser(account);
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [account, active]);
  // mount only once or face issues :P
  const triedEager = useEagerConnect();
  useInactiveListener(!triedEager || !!activatingConnector);

  return (
    <header className="header">
      <div className="header__content">
        <div className="header__logo">
          <Link to="/">
            <img src={logo} alt="" />
          </Link>
          <div className="search-outline d-flex justify-content-center align-items-center ml-5">
            <input
            type="text"
            placeholder="Search..."
            className="search-input"
            // value={searchText}
            // onChange={(e) => handleSearch(e)}
            />
            <button style={{color: 'white', opacity: '.8'}} className="pr-3">
              <svg xmlns="http://www.w3.org/2000/svg" width="14" height="14" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
              </svg>
            </button>
          </div>
        </div>
        <div className="header__menu">
          <ul className="header__nav">
            <li className="header__nav-item">
              <Link
                className="header__nav-link headerActive"
                to="/"
                role="button"
                id="dropdownMenuHome"
                aria-haspopup="true"
                aria-expanded="false"
              >
                Home
              </Link>
            </li>
